import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { motion } from "framer-motion";
import { toast } from "sonner";
import PitchCard from "../components/PitchCard";
import { baseUrl } from "../App";
import {
  MapPinIcon,
  UserIcon,
  ChartBarIcon,
  DocumentTextIcon,
  PhotoIcon,
  QuestionMarkCircleIcon,
} from "@heroicons/react/24/outline";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import axios from "axios";

function EnterpriseDetails() {
  const images = [
    "data:image/jpeg;base64,", // coins in hand
    "data:image/jpeg;base64,", // startup growth chart
    "data:image/jpeg;base64,", // money and graph
    "data:image/jpeg;base64,", // investor handshake
    "data:image/jpeg;base64,", // stack of coins and plant
  ];

  const { id } = useParams();
  const [showInvestModal, setShowInvestModal] = useState(false);
  const [showChatModal, setShowChatModal] = useState(false);
  const [investmentAmount, setInvestmentAmount] = useState("");
  const [campaign, setCampaign] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const invid = localStorage.getItem("id");
  const name = localStorage.getItem("investor");

  useEffect(() => {
    const fetchCampaign = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${baseUrl}/campaigns/${id}`);
        const data = response.data;

        if (!data) {
          throw new Error("Campaign not found");
        }

        // Map database fields to component expected fields
        const mappedCampaign = {
          ...data,
          name: data.title,
          fundingGoal: data.targetAmt,
          businessPlan: data.buisnessPlan,
          financialProjections: data.financialProjec,
          riskFactors: data.riskFactor,
          // Add any additional mappings as needed
        };

        setCampaign(mappedCampaign);
      } catch (err) {
        setError(err.message);
        toast.error("Failed to load campaign details");
      } finally {
        setLoading(false);
      }
    };

    fetchCampaign();
  }, [id]);

  const handleInvestment = () => {
    if (!investmentAmount || isNaN(investmentAmount)) {
      toast.error("Please enter a valid investment amount");
      return;
    }

    // TODO: Implement actual investment logic
    toast.success("Investment request submitted successfully!");
    setShowInvestModal(false);
    setShowChatModal(true);
    setInvestmentAmount("");
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 flex justify-center items-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-400">
            Loading campaign details...
          </p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="card text-center py-8">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
            Unable to load campaign
          </h2>
          <p className="text-gray-600 dark:text-gray-400">{error}</p>
        </div>
      </div>
    );
  }

  if (!campaign) {
    return null;
  }

  // Calculate campaign start and end dates display
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  };

  const startDateDisplay = campaign.startDate
    ? formatDate(campaign.startDate)
    : "Not specified";
  const endDateDisplay = campaign.endDate
    ? formatDate(campaign.endDate)
    : "Not specified";
  const campaignDuration =
    campaign.startDate && campaign.endDate
      ? `${startDateDisplay} - ${endDateDisplay}`
      : "Date range not specified";

  // Select a random image from the images array
  const randomImage = images[Math.floor(Math.random() * images.length)];

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Header */}
      <div className="mb-8">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
              {campaign.name || campaign.title}
            </h1>
            <div className="flex items-center mt-2 space-x-4">
              <span className="text-primary-600 dark:text-primary-400 font-medium">
                {campaign.industry || "Industry not specified"}
              </span>
              <span className="flex items-center text-gray-600 dark:text-gray-400">
                <MapPinIcon className="h-5 w-5 mr-1" />
                {campaign.location || "Location not specified"}
              </span>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2 space-y-8">
          {/* Image Gallery */}
          <div className="card">
            <div className="relative h-96">
              <img
                src={randomImage}
                alt={campaign.name || campaign.title}
                className="w-full h-full object-cover rounded-lg"
              />
            </div>
          </div>

          {/* Business Description */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
              Business Overview
            </h2>
            <p className="text-gray-600 dark:text-gray-400">
              {campaign.description}
            </p>
            <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                Campaign Duration
              </h3>
              <p className="text-gray-600 dark:text-gray-400">
                {campaignDuration}
              </p>
            </div>
          </div>

          {/* Investment Details - New Section */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
              Investment Details
            </h2>
            <div className="grid grid-cols-2 gap-4 mb-6">
              <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
                <h3 className="text-md font-medium text-gray-700 dark:text-gray-300 mb-1">
                  Equity Offered
                </h3>
                <p className="text-xl font-bold text-primary-600">
                  {campaign.equityOffered}%
                </p>
              </div>
              <div className="p-4 bg-gray-50 dark:bg-gray-700 rounded-lg">
                <h3 className="text-md font-medium text-gray-700 dark:text-gray-300 mb-1">
                  Minimum Investment
                </h3>
                <p className="text-xl font-bold text-primary-600">
                ₹{campaign.minInvestmentAmt?.toLocaleString() || 0}
                </p>
              </div>
            </div>

            <div className="border-t border-gray-200 dark:border-gray-700 pt-6 mt-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-3">
                Business Plan
              </h3>
              <div className="prose prose-sm max-w-none text-gray-600 dark:text-gray-400">
                {campaign.businessPlan || campaign.buisnessPlan ? (
                  <p>{campaign.businessPlan || campaign.buisnessPlan}</p>
                ) : (
                  <p className="text-gray-500 italic">
                    No business plan available
                  </p>
                )}
              </div>
            </div>

            <div className="border-t border-gray-200 dark:border-gray-700 pt-6 mt-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-3">
                Financial Projections
              </h3>
              <div className="prose prose-sm max-w-none text-gray-600 dark:text-gray-400">
                {campaign.financialProjections || campaign.financialProjec ? (
                  <p>
                    {campaign.financialProjections || campaign.financialProjec}
                  </p>
                ) : (
                  <p className="text-gray-500 italic">
                    No financial projections available
                  </p>
                )}
              </div>
            </div>

            <div className="border-t border-gray-200 dark:border-gray-700 pt-6 mt-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-3">
                Market Analysis
              </h3>
              <div className="prose prose-sm max-w-none text-gray-600 dark:text-gray-400">
                {campaign.marketAnalysis ? (
                  <p>{campaign.marketAnalysis}</p>
                ) : (
                  <p className="text-gray-500 italic">
                    No market analysis available
                  </p>
                )}
              </div>
            </div>

            <div className="border-t border-gray-200 dark:border-gray-700 pt-6 mt-6">
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-3">
                Risk Factors
              </h3>
              <div className="prose prose-sm max-w-none text-gray-600 dark:text-gray-400">
                {campaign.riskFactors || campaign.riskFactor ? (
                  <p>{campaign.riskFactors || campaign.riskFactor}</p>
                ) : (
                  <p className="text-gray-500 italic">
                    No risk factors available
                  </p>
                )}
              </div>
            </div>
          </div>

          {/* Financial Projections */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
              Financial Performance
            </h2>
            {campaign.financials &&
            campaign.financials.revenue &&
            campaign.financials.revenue.length > 0 ? (
              <div className="h-80">
                <ResponsiveContainer width="100%" height="100%">
                  <LineChart data={campaign.financials.revenue}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="month" />
                    <YAxis />
                    <Tooltip />
                    <Line
                      type="monotone"
                      dataKey="value"
                      stroke="#0284c7"
                      strokeWidth={2}
                    />
                  </LineChart>
                </ResponsiveContainer>
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                <ChartBarIcon className="h-12 w-12 mx-auto mb-2" />
                <p>No financial data available</p>
              </div>
            )}

            {campaign.financials && campaign.financials.metrics && (
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mt-6">
                {Object.entries(campaign.financials.metrics).map(
                  ([key, value]) => (
                    <div key={key} className="text-center">
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        {key.replace(/([A-Z])/g, " ₹1").trim()}
                      </p>
                      <p className="text-lg font-semibold text-gray-900 dark:text-white">
                        {value}
                      </p>
                    </div>
                  )
                )}
              </div>
            )}
          </div>
        </div>

        <div className="space-y-8">
          {/* Funding Progress */}
          <div className="card">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
              Funding Progress
            </h2>
            <div className="mb-4">
              <div className="flex justify-between text-sm mb-1">
                <span className="text-gray-600 dark:text-gray-400">Raised</span>
                <span className="font-medium text-gray-900 dark:text-white">
                ₹{campaign.currentFunding?.toLocaleString() || 0} / ₹
                  {(
                    campaign.fundingGoal || campaign.targetAmt
                  )?.toLocaleString() || 0}
                </span>
              </div>
              <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
                <div
                  className="bg-primary-600 h-2 rounded-full"
                  style={{
                    width: `${
                      campaign.fundingGoal || campaign.targetAmt
                        ? Math.min(
                            ((campaign.currentFunding || 0) /
                              (campaign.fundingGoal || campaign.targetAmt)) *
                              100,
                            100
                          )
                        : 0
                    }%`,
                  }}
                />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div className="text-center">
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  Target Amount
                </p>
                <p className="text-lg font-semibold text-gray-900 dark:text-white">
                ₹
                  {(
                    campaign.fundingGoal || campaign.targetAmt
                  )?.toLocaleString() || 0}
                </p>
              </div>
              <div className="text-center">
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  Min Investment
                </p>
                <p className="text-lg font-semibold text-gray-900 dark:text-white">
                ₹{campaign.minInvestmentAmt?.toLocaleString() || 0}
                </p>
              </div>
            </div>
            <div className="space-y-2">
              <button
                onClick={() => setShowChatModal(true)}
                className="w-full px-4 py-2 rounded-md bg-gray-300 text-gray-800 font-medium hover:bg-gray-300 transition"
              >
                Chat Now
              </button>

              <button
                onClick={() => setShowInvestModal(true)}
                className="w-full px-4 py-2 rounded-md bg-blue-600 text-white font-medium hover:bg-blue-700 transition"
              >
                Invest Now
              </button>
            </div>
          </div>

          {/* Organization Information */}
          {campaign.owner && (
            <div className="card">
              <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
                About the Owner
              </h2>
              <div className="flex items-center mb-4">
                <img
                  src={
                    campaign.owner.image ||
                    "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-1.2.1&auto=format&fit=facearea&facepad=2&w=256&h=256&q=80"
                  }
                  alt={campaign.owner.name}
                  className="h-12 w-12 rounded-full"
                />
                <div className="ml-4">
                  <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                    {campaign.owner.name}
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400">
                    {campaign.owner.experience}
                  </p>
                </div>
              </div>
              {campaign.owner.verified && (
                <div className="flex items-center text-green-600 dark:text-green-400">
                  <svg
                    className="h-5 w-5 mr-2"
                    fill="currentColor"
                    viewBox="0 0 20 20"
                  >
                    <path
                      fillRule="evenodd"
                      d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                      clipRule="evenodd"
                    />
                  </svg>
                  Verified Business Owner
                </div>
              )}
            </div>
          )}
          <PitchCard />
        </div>
      </div>

      {/* Investment Modal */}
      {showInvestModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="bg-white dark:bg-gray-800 rounded-lg p-6 max-w-md w-full"
          >
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
              Invest in {campaign.name || campaign.title}
            </h2>
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-1">
                Investment Amount (Rupees)
              </label>
              <input
                type="number"
                value={investmentAmount}
                onChange={(e) => setInvestmentAmount(e.target.value)}
                className="input-field"
                placeholder="Enter amount"
                min={campaign.minInvestmentAmt || 1000}
              />
              <p className="text-sm text-gray-500 mt-1">
                Minimum investment: ₹
                {campaign.minInvestmentAmt?.toLocaleString() || 1000}
              </p>
            </div>
            <div className="flex space-x-4">
              <button onClick={handleInvestment} className="btn-primary flex-1">
                Confirm Investment
              </button>
              <button
                onClick={() => setShowInvestModal(false)}
                className="btn-secondary flex-1"
              >
                Cancel
              </button>
            </div>
          </motion.div>
        </div>
      )}

      {/* Chat Modal */}
      {showChatModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            className="bg-white dark:bg-gray-800 rounded-lg p-6 max-w-md w-full"
          >
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
              Chat with {campaign.owner?.name || "Business Owner"}
            </h2>
            <div className="h-64 bg-gray-100 dark:bg-gray-700 rounded-lg mb-4 p-4 overflow-auto">
              <p className="text-center text-gray-500 dark:text-gray-400">
                Start a conversation with the campaign owner
              </p>
            </div>
            <div className="flex space-x-2">
              <input
                type="text"
                className="input-field flex-1"
                placeholder="Type your message..."
              />
              <button className="btn-primary px-4">Send</button>
            </div>
            <button
              onClick={() => setShowChatModal(false)}
              className="mt-4 w-full btn-secondary"
            >
              Close
            </button>
          </motion.div>
        </div>
      )}
    </div>
  );
}

export default EnterpriseDetails;
